// Custom clipper for the chat background
class ChatBackgroundClipper extends CustomClipper<Path> {
  @override
  Path getClip(Size size) {
    Path path = Path();

    // Start from top-left
    path.moveTo(0, 0);

    // Go straight to right side where the curve begins (around 80% of width)
    path.lineTo(size.width * 0.8, 0);

    // Create a smooth curve at the top-right corner
    path.quadraticBezierTo(
      size.width * 0.9, // control point x
      size.height * 0.03, // control point y
      size.width, // end point x
      size.height * 0.1, // end point y
    );

    // Go down to bottom-right
    path.lineTo(size.width, size.height);

    // Go to bottom-left
    path.lineTo(0, size.height);

    // Close the path back to top-left
    path.close();

    return path;
  }

  @override
  bool shouldReclip(CustomClipper<Path> oldClipper) => false;
}

class PurpleShapeClipper extends CustomClipper<Path> {
  @override
  Path getClip(Size size) {
    Path path = Path();
    path.moveTo(0, 0);
    path.lineTo(size.width * 0.75, 0);
    path.quadraticBezierTo(
      size.width * 0.83,
      size.height * 0.02,
      size.width * 0.83,
      size.height * 0.08,
    );
    path.quadraticBezierTo(
      size.width * 0.83,
      size.height * 0.14,
      size.width,
      size.height * 0.14,
    );
    path.lineTo(size.width, size.height);
    path.lineTo(0, size.height);
    path.close();
    return path;
  }

  @override
  bool shouldReclip(CustomClipper<Path> oldClipper) => false;
}

