  // Initialize local data first, then try Firebase
  Future<void> _initializeLocalData() async {
    try {
      // First check if we have cached user data
      await _checkCachedUserAsync();

      // Then try to get data from Firebase (will work if online)
      await _checkCurrentUser();
    } catch (e) {
      print("Error initializing local data: $e");
    }
  }

  // Initialize Firebase
  Future<void> _initializeFirebase() async {
    try {
      // Check if user is already signed in
      final user = _firebaseService.currentUser;
      if (user != null) {
        // Get user profile
        final userProfile = await _firebaseService.getUserProfile(user.uid);
        if (userProfile != null) {
          setState(() {
            _currentUser = userProfile;
            _isAuthenticated = true;
            _currentScreen = AppScreen.main;
          });
        }
      }

      setState(() {
        _isInitialized = true;
      });
    } catch (e) {
      print("Error initializing Firebase: $e");
    }
  }

