  // Updated to handle bottom navigation bar index change with animation
  void _onNavItemTapped(int index) {
    if (_currentIndex == index) {
      // If tapping the same tab, just refresh the current tab data
      setState(() {});
      return;
    }

    setState(() {
      // Save previous index for potential animation
      final previousIndex = _currentIndex;
      _currentIndex = index;

      // Clear selected chat when switching tabs
      if (previousIndex != index) {
        _selectedChat = null;
        _selectedContact = null;
        // Hide search when switching tabs
        _isSearchVisible = false;
        _searchController.clear();
        _searchQuery = '';
        _searchResults.clear();
      }
    });
  }

  // Toggle search visibility
  void _toggleSearch() {
    setState(() {
      _isSearchVisible = !_isSearchVisible;
      if (!_isSearchVisible) {
        _searchController.clear();
        _searchQuery = '';
        _searchResults.clear();
      } else {
        // If in private chat, make sure to focus the search field
        if (_currentIndex == 0) {
          // Delay to allow UI to update
          Future.delayed(const Duration(milliseconds: 100), () {
            FocusScope.of(context).requestFocus(_searchFocusNode);
          });
        }
      }
    });
  }

  Future<void> _checkCurrentUser() async {
    print("--- _checkCurrentUser started ---");

    // Don't set _isLoading to true if we already have a user from local database
    if (_currentUser == null) {
      setState(() {
        _isLoading = true;
        _errorMessage = null;
      });
    }

    try {
      // Check if user is already signed in
      final user = _firebaseService.currentUser;
      print("--- Current user: ${user?.uid} ---");

      // Check connectivity status
      final isConnected = await _connectivityService.checkConnectivity();

      if (user != null) {
        // Try to get user profile from Firestore if online
        if (isConnected) {
          try {
            final firebaseUser =
                await _firebaseService.getUserProfile(user.uid);
            if (firebaseUser != null) {
              print("--- User profile from Firebase: ${firebaseUser.name} ---");

              // Save user to local database for offline access
              await _localDb.saveUser(firebaseUser);

              // Update current user if we don't have one or if Firebase has newer data
              if (_currentUser == null ||
                  (_currentUser?.lastSeen == null ||
                      firebaseUser.lastSeen != null &&
                          firebaseUser.lastSeen!
                              .isAfter(_currentUser!.lastSeen!))) {
                setState(() {
                  _currentUser = firebaseUser;
                });
              }
            }
          } catch (e) {
            print("Error getting user profile from Firebase: $e");
          }
        }

        // If we still don't have a user, try local database
        if (_currentUser == null) {
          final localUser = await _localDb.getUserById(user.uid);
          if (localUser != null) {
            print("--- User profile from local DB: ${localUser.name} ---");
            setState(() {
              _currentUser = localUser;
            });
          }
        }
      }

      if (_currentUser != null) {
        // Initialize Firestore collections if online
        if (isConnected) {
